mapper.update:
 update SYS_DEPARTMENT_INFO
 	set
     DEPARTMENT_NAME = #{departmentName,jdbcType=VARCHAR},
     DEPARTMENT_AREA_ID = #{departmentAreaId,jdbcType=VARCHAR}
		where DEPARTMENT_ID = #{departmentId,jdbcType=VARCHAR}

mapper.select:
   	select * from (select a.*, rownum rnum from (select 
    t.DEPARTMENT_ID,t2.area_name||'--'||t.department_name DEPARTMENT_NAME,  t.PARENT_DEPARTMENT_ID,  t.DEPARTMENT_CODE,  t.DEPARTMENT_AREA_ID,t.is_delete
    from sys_department_info t ,SYS_AREA_INFO t2
	where t.department_area_id=t2.area_id and t.IS_DELETE = 0 


          	and DEPARTMENT_NAME like '%'||#{queryBean.departmentName}||'%'


          	and DEPARTMENT_AREA_ID = #{queryBean.departmentAreaId}

    ) a

		where rownum <=  #{endRow} 

	)

		where rnum >  #{beginRow}

 	 select *
 	 from (select a.*, rownum rnum
          from (select t.DEPARTMENT_ID,
                         t2.area_name || '--' || t.department_name DEPARTMENT_NAME,
                         t.PARENT_DEPARTMENT_ID,
                         t.DEPARTMENT_CODE,
                         t.DEPARTMENT_AREA_ID,
                         t.is_delete
                    from sys_department_info t, SYS_AREA_INFO t2
                   where t.department_area_id = t2.area_id
                     and t.IS_DELETE = 0 and t2.is_delete=0


	         		and department_id like '%'||#{queryBean.departmentId}||'%'


          		and DEPARTMENT_AREA_ID  = #{queryBean.departmentAreaId}


                   	 order by DEPARTMENT_NAME
          ) a

		where rownum <=  #{endRow} 

	)

		where rnum >  #{beginRow}

select count(*) from (select t.DEPARTMENT_ID, t2.area_name || '--' || t.department_name DEPARTMENT_NAME, t.PARENT_DEPARTMENT_ID, t.DEPARTMENT_CODE, t.DEPARTMENT_AREA_ID, t.is_delete from sys_department_info t, SYS_AREA_INFO t2 where t.department_area_id = t2.area_id and t.IS_DELETE = 0
and department_area_id = #{queryBean.departmentAreaId}
)

select count(1) from (select t.DEPARTMENT_ID, t2.area_name || '--' || t.department_name DEPARTMENT_NAME, t.PARENT_DEPARTMENT_ID, t.DEPARTMENT_CODE, t.DEPARTMENT_AREA_ID, t.is_delete from sys_department_info t, SYS_AREA_INFO t2 where t.department_area_id = t2.area_id and t.IS_DELETE = 0
and department_id like '%'||#{queryBean.departmentId}||'%'
and DEPARTMENT_AREA_ID = #{queryBean.departmentAreaId}
)
