mapper.select:
  	SELECT count(1)
	FROM (SELECT  ROW_NUMBER() OVER(PARTITION BY t.NAME ORDER BY length(t.NAME) DESC,ROWNUM) RN,t.* 
     	FROM SYS_COMPANY_INFO t where t.IS_DELETE ='0'
     	) c
	WHERE c.IS_DELETE ='0' and c.RN = 1 and status is not  null and  length(name)>3


	        AND c.NAME LIKE '%'||#{queryBean.name}||'%'   


	        AND c.IS_ABOVE_SCALE = #{queryBean.isAboveScale}   


	        AND c.IS_CANVASS_BUISINESS = #{queryBean.isCanvassBuisiness}

  	SELECT *
    FROM  (SELECT a.*, rownum rnum
     FROM (
     	SELECT c.*  
     	FROM (
     	SELECT  ROW_NUMBER() OVER(PARTITION BY t.NAME ORDER BY length(t.NAME) DESC,ROWNUM) RN,t.* 
     	FROM SYS_COMPANY_INFO t where t.IS_DELETE ='0'
     	) c
		WHERE c.IS_DELETE ='0' and c.RN = 1 and status is not  null and  length(name)>3


	         AND c.NAME LIKE '%'||#{queryBean.name}||'%'   


	         AND c.IS_ABOVE_SCALE = #{queryBean.isAboveScale}   


	         AND c.IS_CANVASS_BUISINESS = #{queryBean.isCanvassBuisiness}   


			ORDER BY c.NAME DESC
		) a

	WHERE rownum <=  #{endRow} 

	)

	WHERE rnum >  #{beginRow}

     	SELECT count(1)  
     	FROM SYS_COMPANY_INFO
     	WHERE 1=1	

	        AND NAME = #{name,jdbcType=VARCHAR}   


	        AND ID != #{id,jdbcType=DECIMAL}
